import React from 'react';
import '../Styles/SideMenuBar.css';
import { SidebarData } from './SidebarData';

function SideMenuBar() {
  return (
    <div className='sidebar sidebar-left'>
      <div className='sidebar-top'>
        <div className='logo'>
          <img src='CompanyLogo.png' alt='Logo' />
        </div>
      </div>
      <ul className='sidebarList'>
      {SidebarData.map((val,key)=>{
  return ( 
    <li key={key} 
        onClick={ () => {window.location.pathname=val.link} }
        className='row'
        id={window.location.pathname === val.link? "active" : ""}
    >
      <div id='icon'>
        {val.icon}
      </div>{" "}
      <div id='title' className={window.location.pathname === val.link? "selected" : ""}>
        {val.title}
      </div>
      <div className="indicator" style={{backgroundColor: window.location.pathname === val.link? "#FFA500" : "transparent"}}></div>
    </li>
  );
})}
      </ul>
      <div className='sidebar-bottom'>
        <div className='profile'>
          <img src='https://dummyimage.com/50x50/000/fff&text=Profile' alt='Profile' />
          <span>karthi</span>
        </div>
      </div>
    </div>
  )
}

export default SideMenuBar
