import React, { useState, useEffect, useMemo } from 'react';
import Table from '../Components/CustomTable';
import PaginationRounded from '../Components/TablePagination';

const CustomerList = () => {
  const [customers, setCustomers] = useState([]);
  const [currentPage, setCurrentPage] = useState(1);
  const [itemsPerPage, setItemsPerPage] = useState(10);

  useEffect(() => {
    fetch('https://jsonplaceholder.typicode.com/users')
      .then(response => response.json())
      .then(data => setCustomers(data))
  }, []);

  const displayedData = useMemo(() => {
    const indexOfLastItem = currentPage * itemsPerPage;
    const indexOfFirstItem = indexOfLastItem - itemsPerPage;
    return customers.slice(indexOfFirstItem, indexOfLastItem);
  }, [customers, currentPage, itemsPerPage]);

  const columns = useMemo(
    () => [
      {
        Header: 'ID',
        accessor: 'id',
      },
      {
        Header: 'Name',
        accessor: 'name',
      },
      {
        Header: 'Email',
        accessor: 'email',
      },
      {
        Header: 'Phone',
        accessor: 'phone',
      },
    ],
    []
  );

  const handleRowClick = (row) => {
    console.log('Clicked row:', row.original.id);
  };

  const handleItemsPerPageChange = (value) => {
    setItemsPerPage(value);
    setCurrentPage(1);
  };

  const handlePageChange = (page) => {
    setCurrentPage(page);
  };

  const pageCount = Math.ceil(customers.length / itemsPerPage);

  return (
    <div className="container">
      <h1>Customer List</h1>
      <Table title="Customers" data={displayedData} columns={columns} onRowClick={handleRowClick} style={{ marginBottom: '20px' }} />
      <div className="pagination-container">
        <PaginationRounded
          currentPage={currentPage}
          totalPages={pageCount}
          onPageChange={handlePageChange}
          itemsPerPage={itemsPerPage}
          onItemsPerPageChange={handleItemsPerPageChange}
        />
      </div>
    </div>
  );
};

export default CustomerList;
