import React, { useState, useEffect } from 'react';
import Table from '../Components/CustomTable';
import PaginationRounded from '../Components/TablePagination';
import '../Styles/CustomersList.css';

const CustomerList = () => {
  const [data, setData] = useState([]);
  const [currentPage, setCurrentPage] = useState(1);
  const [itemsPerPage] = useState(10);


  useEffect(() => {
    fetch('https://jsonplaceholder.typicode.com/posts')
      .then(response => response.json())
      .then(data => setData(data.slice(0, 100)))
  }, []);

  const columns = React.useMemo(
    () => [
      {
        Header: 'ID',
        accessor: 'id',
      },
      {
        Header: 'Name',
        accessor: 'ttle',
      },
      {
        Header: 'Package',
        accessor: 'b',
      },
      {
        Header: 'Vouchers Purchased',
        accessor: 'bod',
      },
      {
        Header: 'Subscription Expiry Date',
        accessor: 'bo',
      },
    ],
    []
  );

  const handleChangePage = (page) => {
    setCurrentPage(page);
  };

  const pageCount = Math.ceil(data.length / itemsPerPage);

  const displayedData = React.useMemo(() => {
    const start = (currentPage - 1) * itemsPerPage;
    const end = start + itemsPerPage;
    return data.slice(start, end);
  }, [currentPage, data, itemsPerPage]);


  return (
    <div>
      <Table
        title="All Customers"
        data={displayedData}
        columns={columns}
        tableClassName='table'
        titleClassName='table-title'
        cardClassName='card'
      />
      <div className="pagination-container">
        <PaginationRounded
          currentPage={currentPage}
          totalPages={pageCount}
          onPageChange={handleChangePage}
          className="pagination"
        />
      </div>
    </div>
  );
};

export default CustomerList;
