import React, { useState, useEffect } from 'react';
import Table from '../Components/CustomTable';
import PaginationRounded from '../Components/TablePagination';
import { useNavigate } from 'react-router-dom';
import { useLocation } from 'react-router-dom';
import { Tab, Nav } from 'react-bootstrap';

const MyTable = () => {

  const [data, setData] = useState([]);
  const [currentPage, setCurrentPage] = useState(1);
  const [itemsPerPage] = useState(10);

  const navigate = useNavigate();


  useEffect(() => {
    fetch('https://jsonplaceholder.typicode.com/posts')
      .then(response => response.json())
      .then(data => setData(data.slice(0, 100)))
  }, []);

  const columns = React.useMemo(
    () => [
      {
        Header: 'ID',
        accessor: 'id',
      },
      {
        Header: 'Title',
        accessor: 'title',
      },
    ],
    []
  );

  const handleChangePage = (page) => {
    setCurrentPage(page);
  };

  const pageCount = Math.ceil(data.length / itemsPerPage);

  const displayedData = React.useMemo(() => {
    const start = (currentPage - 1) * itemsPerPage;
    const end = start + itemsPerPage;
    return data.slice(start, end);
  }, [currentPage, data, itemsPerPage]);

  const handleRowClick = (row) => {
    console.log('Clicked row:', row.original.id);
    navigate(`/customerInfo/${row.original.id}`);
  };

  return (
    <div>
      <Table title="Posts" data={displayedData} columns={columns} onRowClick={handleRowClick} />
      <div className="pagination">
        <PaginationRounded
          currentPage={currentPage}
          totalPages={pageCount}
          onPageChange={handleChangePage}
          pageStyle={{ color: '#F4A26C' }}
          selectedPageStyle={{ color: '#F4A26C', fontWeight: 'bold' }}

        />
        <div style={{ position: 'absolute', bottom: '0', width: '100%', paddingBottom: '30px' }}>
      <Tab.Container activeKey={activeTab} onSelect={handleTabSelect}>
        <Nav variant="tabs">
          <Nav.Item>
            <Nav.Link eventKey="details">Details</Nav.Link>
          </Nav.Item>
          <Nav.Item>
            <Nav.Link eventKey="orders">Orders</Nav.Link>
          </Nav.Item>
          <Nav.Item>
            <Nav.Link eventKey="notes">Notes</Nav.Link>
          </Nav.Item>
        </Nav>
        <Tab.Content>
          <Tab.Pane eventKey="details">
            <div>Customer Details</div>
          </Tab.Pane>
          <Tab.Pane eventKey="orders">
            <div>Customer Orders</div>
          </Tab.Pane>
          <Tab.Pane eventKey="notes">
            <div>Customer Notes</div>
          </Tab.Pane>
        </Tab.Content>
      </Tab.Container>
    </div>
      </div>
    </div>
  );
};

export default MyTable;
