import { useParams, useNavigate } from 'react-router-dom';
import { useEffect, useState } from 'react';
import '../Styles/CustomerInfo.css';

const CustomerInfo = () => {
  const [customer, setCustomer] = useState({});
  const { id } = useParams();
  const navigate = useNavigate();

  useEffect(() => {
    fetch(`https://jsonplaceholder.typicode.com/users/${id}`)
      .then(response => response.json())
      .then(data => setCustomer(data))
      .catch(error => console.error(error));
  }, [id]);

  const handleClick = () => {
    navigate(-1);
  };

  return (
    <div className="customer-info">
      <h2>Customer Details</h2>
      {customer && customer.id ? (
        <div className="customer-details">
          <p><span>Name:</span> {customer.name}</p>
          <p><span>Username:</span> {customer.username}</p>
          <p><span>Email:</span> {customer.email}</p>
          <p><span>Phone:</span> {customer.phone}</p>
          <p><span>Website:</span> {customer.website}</p>
          <button onClick={handleClick}>Back to Customers</button>
        </div>
      ) : (
        <p>Loading customer details...</p>
      )}
    </div>
  );
};

export default CustomerInfo;
