import React from "react";
import CenteredTabs from "../Components/ThreeTabs";
import "../Styles/CustomerInfo.css";
import SubscriptionVoucher from "./Subscription-Voucher";
import SubscriptionCustomer from "./Subscription-Customer";
import ArrowBackIcon from "@mui/icons-material/ArrowBack";
import { makeStyles } from "@mui/styles";
import { useNavigate } from "react-router-dom";

const useStyles = makeStyles({
  arrowIcon: {
    fontSize: "40px",
    cursor: "pointer",
    color: "#1B223E",
  },
});

const SubscriptionInfo = () => {
  const tabs = [
    { label: "Info" },
    { label: "Customers" },
    { label: "Voucher" },
  ];

  const tabComponents = [
    SubscriptionVoucher,
    SubscriptionCustomer,
    SubscriptionVoucher,
  ];

  const classes = useStyles();

  const navigate = useNavigate();

  const handleClick = () => {
    navigate("/SubscriptionList");
  };

  return (
    <div>
      <div className="back-button">
        <ArrowBackIcon className={classes.arrowIcon} onClick={handleClick} />
        <h1 className="customer-name">Karthi</h1>
      </div>
      <div className="tabs-container">
        <CenteredTabs tabs={tabs} tabComponents={tabComponents} />
      </div>
    </div>
  );
};

export default SubscriptionInfo;
