import React from "react";
import CenteredTabs from "../Components/ThreeTabs";
import "../Styles/CustomerInfo.css";
import CustomerVoucher from "./CustomerVoucher";
import CustomerSubscription from "./CustomerSubscription";
// import { makeStyles } from '@mui/styles';

// const useStyles = makeStyles({
//   arrowIcon: {
//     fontSize: '40px',
//     cursor:"pointer",
//     color: '#1B223E',
//   },
// });

// const classes = useStyles();

const MyTable = () => {

  const tabs = [
    { label: "Info" },
    { label: "Subscription" },
    { label: "Voucher" },
  ];

  const tabComponents = [
    CustomerSubscription,
    CustomerSubscription,
    CustomerVoucher,
  ];

  const navigate = useNavigate();

  const handleClick = () => {
    navigate("/customersList");
  };

  return (
    <div>
      <div className="back-button">
        <ArrowBackIcon className={classes.arrowIcon} onClick={handleClick} />
        <h1 className="customer-name">Karthi</h1>
      </div>
      <div className="tabs-container">
        <CenteredTabs tabs={tabs} tabComponents={tabComponents} />
      </div>
    </div>
  );
};

export default MyTable;
