import React from 'react';
import { useTable, usePagination } from 'react-table';
import '../Styles/CustomTable.css';
import PaginationRounded from './TablePagination';

const Table = ({ title, data, columns, onRowClick }) => {
  const {
    getTableProps,
    getTableBodyProps,
    headerGroups,
    prepareRow,
    page,
    state: { pageIndex, pageSize },
  } = useTable(
    {
      columns,
      data,
      initialState: { pageIndex: 0, pageSize: 10 },
    },
    usePagination
  );

  const handleChangePage = (page) => {
    gotoPage(page);
  };

  return (
    <div className="table">
      <h2 className="table-title">{title}</h2>
      <div className="card">
        <table {...getTableProps()}>
          <thead>
            {headerGroups.map((headerGroup) => (
              <tr {...headerGroup.getHeaderGroupProps()}>
                {headerGroup.headers.map((column) => (
                  <th {...column.getHeaderProps()}>{column.render('Header')}</th>
                ))}
              </tr>
            ))}
          </thead>
          <tbody {...getTableBodyProps()}>
            {page.map((row, i) => {
              prepareRow(row);
              return (
                <tr {...row.getRowProps()} onClick={() => onRowClick(row)}>
                  {row.cells.map((cell) => {
                    return <td {...cell.getCellProps()}>{cell.render('Cell')}</td>;
                  })}
                </tr>
              );
            })}
          </tbody>
        </table>
      </div>
      <div className="pagination-container" style={{ justifyContent: 'center' }}>
        <PaginationRounded
          currentPage={pageIndex + 1}
          totalPages={Math.ceil(data.length / pageSize)}
          onPageChange={handleChangePage}
          pageStyle={{ color: '#F4A26C' }}
          selectedPageStyle={{ color: '#F4A26C', fontWeight: 'bold' }}
        />
      </div>
    </div>
  );
};

export default Table;
