import React, { useState } from "react";
import PropTypes from "prop-types";
import { styled } from "@mui/material/styles";
import Tabs from "@mui/material/Tabs";
import Tab from "@mui/material/Tab";
import Box from "@mui/material/Box";
import CustomerSubscription from "../Customer-Screens/CustomerSubscription";
import MyTable from "../Customer-Screens/CustomerInfo";

const MyTabs = styled(Tabs)({
  "& .MuiTabs-indicator": {
    backgroundColor: "##000000",
  },
});

const MyTab = styled(Tab)({
  textTransform: "none",
  fontSize: "14px",
  fontWeight: "bold",
  padding: "12px",
  color: "#000000",
  "&.Mui-selected": {
    color: "#ffffff",
    backgroundColor: "#000000",
  },
});

function CenteredTabs(props) {
  const { tabs } = props;
  const [selectedTab, setSelectedTab] = useState(0);

  const handleTabChange = (event, newValue) => {
    setSelectedTab(newValue);
  };

  return (
    <Box sx={{ width: "100%" }}>
      <MyTabs value={selectedTab} onChange={handleTabChange} centered>
        {tabs.map((tab, index) => (
          <MyTab key={index} label={tab.label} />
        ))}
      </MyTabs>
      {selectedTab === 0 && <CustomerSubscription />}
      {/* {selectedTab === 1 && <MyTable />} */}
      {selectedTab === 2 && <CustomerSubscription />}

    </Box>
  );
}

CenteredTabs.propTypes = {
  tabs: PropTypes.arrayOf(
    PropTypes.shape({
      label: PropTypes.string.isRequired,
    })
  ).isRequired,
};

export default CenteredTabs;
