import React from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import '../Styles/CustomerInfo.css';

const CustomerInfo = ({ data }) => {
  const { id } = useParams();
  const navigate = useNavigate();

  const customer = data.find((item) => item.id === parseInt(id));

  const handleBackClick = () => {
    navigate(-1);
  };

  return (
    <div className="container">
      <div className="customer-info-card">
        <div className="customer-info-header">
          <h2>Customer Info</h2>
          <button className="back-button" onClick={handleBackClick}>Back</button>
        </div>
        <div className="customer-info-body">
          <div className="customer-info-row">
            <div className="customer-info-label">ID:</div>
            <div className="customer-info-value">{customer.id}</div>
          </div>
          <div className="customer-info-row">
            <div className="customer-info-label">Name:</div>
            <div className="customer-info-value">{customer.name}</div>
          </div>
          <div className="customer-info-row">
            <div className="customer-info-label">Email:</div>
            <div className="customer-info-value">{customer.email}</div>
          </div>
          <div className="customer-info-row">
            <div className="customer-info-label">Phone:</div>
            <div className="customer-info-value">{customer.phone}</div>
          </div>
          <div className="customer-info-row">
            <div className="customer-info-label">Website:</div>
            <div className="customer-info-value">{customer.website}</div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default CustomerInfo;
