import React, { useEffect, useState } from "react";
import "../Styles/SideMenuBar.css";
import { SidebarData } from "./SidebarData";
import CompanyLogo from '../images/CompanyLogo.png';
import LoginImage from '../images/imageAi.png';

function SideMenuBar() {
  const [currentPath, setCurrentPath] = useState(window.location.pathname);

  useEffect(() => {
    const handleLocationChange = () => {
      setCurrentPath(window.location.pathname);
    };

    window.addEventListener("popstate", handleLocationChange);

    return () => {
      window.removeEventListener("popstate", handleLocationChange);
    };
  }, []);

  return (
    <div className="sidebar sidebar-left">
      <div className="sidebar-top">
        <div className="logo">
          <img src={CompanyLogo} alt="Logo" />
        </div>
      </div>
      <ul className="sidebarList">
        {SidebarData.map((val, key) => {
          const isSelected = currentPath === val.link;
          const className = `row ${isSelected ? "selected" : ""}`;
          return (
            <li
              key={key}
              onClick={() => {
                window.history.pushState(null, "", val.link);
                setCurrentPath(val.link);
              }}
              className={className}
            >
              <div id="icon">{val.icon}</div>
              <div id="title">{val.title}</div>
              {isSelected && <div className="indicator" />}
            </li>
          );
        })}
      </ul>
      <div className="profile-container">
        <img src={LoginImage} alt="Profile" className="profileImg" />
        <span className="profileName">John Doe</span>
      </div>
    </div>
  );
}

export default SideMenuBar;
