import React, { useState, useEffect } from 'react';
import Table from '../Components/CustomTable';

const PAGE_SIZE = 10;

const CustomersList = ({ history }) => {
  const [customers, setCustomers] = useState([]);
  const [page, setPage] = useState(0);

  useEffect(() => {
    fetch(`https://fakestoreapi.com/users?limit=${PAGE_SIZE}&offset=${page * PAGE_SIZE}`)
      .then(response => response.json())
      .then(data => setCustomers(data));
  }, [page]);

  const columns = [
    {
      Header: 'ID',
      accessor: 'id',
    },
    {
      Header: 'Name',
      accessor: 'name',
    },
    {
      Header: 'Email',
      accessor: 'email',
    },
    {
      Header: 'Address',
      accessor: 'address.street',
      Cell: ({ value }) => `${value}, ${customers[0]?.address.city}`,
    },
  ];

  const handleRowClick = row => {
    const customerId = row.original.id;
    history.push(`/customerInfo/${customerId}`);
  };

  const pageCount = Math.ceil(100 / PAGE_SIZE); // assuming there are 100 total customers

  return (
    <>
      <Table columns={columns} data={customers} onRowClick={handleRowClick} />
      <div className="pagination">
        {Array.from({ length: pageCount }).map((_, i) => (
          <button key={i} onClick={() => setPage(i)}>
            {i + 1}
          </button>
        ))}
      </div>
    </>
  );
};

export default CustomersTable;
