import React, { useState } from "react";
import PropTypes from "prop-types";
import { styled } from "@mui/material/styles";
import Tabs from "@mui/material/Tabs";
import Tab from "@mui/material/Tab";
import Box from "@mui/material/Box";
import CustomerSubscription from "../Customer-Screens/CustomerSubscription";

const MyTabs = styled(Tabs)({
  "& .MuiTabs-indicator": {
    backgroundColor: "#EE9179",
  },
});

const MyTab = styled(Tab)(({ isSelected }) => ({
  textTransform: "none",
  fontSize: "14px",
  fontWeight: "bold",
  padding: "10px 20px",
  color: isSelected ? "#" : "#7B7B7B",
  border: '1px solid white',
  borderRadius: '10px',
  boxShadow: isSelected ? "0px 0px 12px rgba(238, 145, 121, 0.4), 0px 0px 0px 4px rgba(255, 255, 255, 0.2)" : "none",
  "&.Mui-selected": {
    color: "#000000",
  },
}));

const TabContainer = styled(Box)({
  display: "table",
  width: "100%",
  tableLayout: "fixed",
});

const TabWrapper = styled(Box)({
  display: "table-cell",
  textAlign: "center",
  verticalAlign: "middle",
  borderBottom: "2px solid transparent",
  "&:hover": {
    borderBottom: "2px solid #EE9179",
    cursor: "pointer",
  },
  "&.Mui-selected": {
    borderBottom: "2px solid #EE9179",
    cursor: "default",
  },
});

function CenteredTabs(props) {
  const { tabs } = props;
  const [selectedTab, setSelectedTab] = useState(0);

  const handleTabChange = (event, newValue) => {
    setSelectedTab(newValue);
  };

  return (
    <TabContainer>
      <MyTabs value={selectedTab} onChange={handleTabChange}>
        {tabs.map((tab, index) => (
          <MyTab key={index} label={tab.label} isSelected={selectedTab === index} />
        ))}
      </MyTabs>
      {tabs.map((tab, index) => (
        <TabWrapper key={index} isSelected={selectedTab === index} onClick={() => setSelectedTab(index)}>
          {tab.content}
        </TabWrapper>
      ))}
    </TabContainer>
  );
}

CenteredTabs.propTypes = {
  tabs: PropTypes.arrayOf(
    PropTypes.shape({
      label: PropTypes.string.isRequired,
      content: PropTypes.node.isRequired,
    })
  ).isRequired,
};

export default CenteredTabs;
