import React, { useState } from "react";
import InputField from "../Components/CustomInput";

function CustomerEdit() {
  const [name, setName] = useState("");
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [nameError, setNameError] = useState("");
  const [emailError, setEmailError] = useState("");
  const [passwordError, setPasswordError] = useState("");

  const validateInputs = () => {
    let hasError = false;

    // Validate name
    if (!name) {
      setNameError("Name is required.");
      hasError = true;
    } else {
      setNameError("");
    }

    // Validate email
    const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
    if (!email || !emailRegex.test(email)) {
      setEmailError("Email is invalid.");
      hasError = true;
    } else {
      setEmailError("");
    }

    // Validate password
    if (!password || password.length < 8) {
      setPasswordError("Password must be at least 8 characters long.");
      hasError = true;
    } else {
      setPasswordError("");
    }

    return !hasError;
  };

  const handleNameChange = (e) => {
    setName(e.target.value);
  };

  const handleEmailChange = (e) => {
    setEmail(e.target.value);
  };

  const handlePasswordChange = (e) => {
    setPassword(e.target.value);
  };

  const handleSubmit = (e) => {
    e.preventDefault();

    if (validateInputs()) {
      // Submit form
    }
  };

  const isSubmitDisabled = !validateInputs();

  return (
    <div>
      <form onSubmit={handleSubmit}>
        <InputField label="Name" type="text" value={name} onChange={handleNameChange} error={nameError} />
        {/* <InputField label="Email" type="email" value={email} onChange={handleEmailChange} error={emailError} />
        <InputField label="Password" type="password" value={password} onChange={handlePasswordChange} error={passwordError} /> */}
        <button type="submit" disabled={isSubmitDisabled}>Submit</button>
      </form>
    </div>
  );
}

export default CustomerEdit;
