import React, { useState } from "react";
import InputField from "./InputField";

function CustomerEdit() {
  const [firstName, setFirstName] = useState("");
  const [lastName, setLastName] = useState("");
  const [email, setEmail] = useState("");
  const [phone, setPhone] = useState("");
  const [firstNameError, setFirstNameError] = useState("");
  const [lastNameError, setLastNameError] = useState("");
  const [emailError, setEmailError] = useState("");
  const [phoneError, setPhoneError] = useState("");
  const [isSubmitDisabled, setIsSubmitDisabled] = useState(true);

  const validateInputs = () => {
    let hasError = false;

    // Validate first name
    if (!firstName) {
      setFirstNameError("First name is required.");
      hasError = true;
    } else {
      setFirstNameError("");
    }

    // Validate last name
    if (!lastName) {
      setLastNameError("Last name is required.");
      hasError = true;
    } else {
      setLastNameError("");
    }

    // Validate email
    const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
    if (!email || !emailRegex.test(email)) {
      setEmailError("Email is invalid.");
      hasError = true;
    } else {
      setEmailError("");
    }

    // Validate phone number
    const phoneRegex = /^\d{10}$/;
    if (!phone || !phoneRegex.test(phone)) {
      setPhoneError("Phone number is invalid.");
      hasError = true;
    } else {
      setPhoneError("");
    }

    setIsSubmitDisabled(hasError);

    return !hasError;
  };

  const handleFirstNameChange = (e) => {
    setFirstName(e.target.value);
  };

  const handleLastNameChange = (e) => {
    setLastName(e.target.value);
  };

  const handleEmailChange = (e) => {
    setEmail(e.target.value);
  };

  const handlePhoneChange = (e) => {
    setPhone(e.target.value);
  };

  const handleSubmit = (e) => {
    e.preventDefault();

    if (validateInputs()) {
      // Send data to backend
      fetch("http://localhost:3006/saveData", {
        method: "PUT",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify({
          firstName,
          lastName,
          email,
          phone,
        }),
      })
        .then((response) => response.json())
        .then((data) => {
          console.log("Data saved successfully!", data);
        })
        .catch((error) => {
          console.error("Error saving data", error);
        });
    }
  };

  return (
    <div>
      <form onSubmit={handleSubmit}>
        <Input label="First Name" type="text" value={firstName} onChange={handleFirstNameChange} error={firstNameError} />
        <InputField label="Last Name" type="text" value={lastName} onChange={handleLastNameChange} error={lastNameError} />
        <InputField label="Email" type="email" value={email} onChange={handleEmailChange} error={emailError} />
        <InputField label="Phone Number" type="tel" value={phone} onChange={handlePhoneChange} error={phoneError} />
        
        <button type="submit" disabled={isSubmitDisabled}>Save</button>
      </form>
    </div>
  );
}

export default CustomerEdit;
